package xxsurajbxx.game;import xxsurajbxx.game.FramesPerSecond;import xxsurajbxx.game.GameLoop;public abstract class Game implements Runnable {    public static int WIDTH, HEIGHT;	protected String TITLE;    private Thread thread;    protected Game(int width, int height, String title, int fps) {        WIDTH = width;        HEIGHT = height;        TITLE = title;        FramesPerSecond.setFPSCap(fps);    }    public void run() {        GameLoop.loop(this);    }    public abstract void update();    protected synchronized void start() {        thread = new Thread(this);        thread.start();    }}