package xxsurajbxx.game;import java.awt.image.BufferedImage;import java.io.File;
import java.io.IOException;import javax.imageio.ImageIO;
public class SpriteSheet {    protected Sprite sprites[];    protected BufferedImage sheet;    private int numofSprites=0;    public SpriteSheet(String path, int maxNumofSprites) {        try {sheet = ImageIO.read(new File(path));}        catch(IOException e) {e.printStackTrace();}        sprites = new Sprite[maxNumofSprites];    }    public void addSprite(int x, int y, int width, int height, double tim) {        sprites[numofSprites++] = new Sprite(x, y, width, height, tim);    }    public BufferedImage getSpriteImage(int SpriteNumber) {        if(SpriteNumber<numofSprites) {            return sheet.getSubimage(sprites[SpriteNumber].x, sprites[SpriteNumber].y, sprites[SpriteNumber].width, sprites[SpriteNumber].height);        }        return null;    }    public Sprite getSprite(int SpriteNumber) {        if(SpriteNumber<numofSprites) {            return sprites[SpriteNumber];        }        return null;    }    public static class Sprite {        public int x, y, width, height;        public long time;        public Sprite(int x, int y, int width, int height, double secondsToBePlayed) {            this.x = x;            this.y = y;            this.width = width;            this.height = height;            this.time = (long)(secondsToBePlayed*1000000000);        }    }}